export default {
  state() {
    return {
      isAdmin: false,
      user: null
    };
  },
  mutations: {
    isAdmin(state, payload) {
      state.isAdmin = payload.val
    },
    setUser(state, payload) {
      state.user = payload.val
    }
  },
  actions: {
    isAdmin(context, val) {
      context.commit('isAdmin', { val: val });
    },
    setUser(context, val) {
      context.commit('setUser', { val: val });
    },
  },
  getters: {
    isAdmin(state) {
      return state.isAdmin;
    },
    user(state) {
      return state.isAuserdmin;
    },
  }
}
